import { Component } from '@angular/core';

@Component({
  selector: 'app-sim-plans',
  templateUrl: './sim-plans.component.html',
  styleUrls: ['./sim-plans.component.css']
})
export class SimPlansComponent {
  aObj:Category;
  cList: Category[] = [];
  selectedCategory: Category | undefined;

  constructor(private categoryService: CategoryService,private router:Router,private addcart:OrderService) {
    this.aObj=new Admin();
   }

  ngOnInit() {            
    this.getCategories();
    this.getCategories1();
    this.getCategories2();
  }

  getCategories(){
    const category:String="Fruits";
    this.categoryService.getCategory(category).subscribe(
      data=>{
        this.cList=data;

      },
      err=>{
        console.log(err);
      }
    )
  }
  getCategories1(){
    const category:String="Rice";
    this.categoryService.getCategory(category).subscribe(
      data=>{
        this.cList=data;
      },err=>{
        console.log(err)
      }
    )
  }
  getCategories2(){
    const category:String="vegetables";
    this.categoryService.getCategory(category).subscribe(
      data=>{
        this.cList=data;
      },
      err=>{
        console.log(err);
      }
    )
  }

