  // }
  viewProduct(category:any){
    localStorage.setItem("selectedProduct",category)
    console.log(category)
    this.router.navigate(['viewproduct'])
  }


  isBouncing: boolean = false;

  startBounceAnimation() {
    this.isBouncing = true;
